import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Router } from '@angular/router';
import { EstudanteService } from '../../../servicos/estudante.service';
import { Estudante } from '../../../modelos/estudante';
import { LocalidadeService } from '../../../servicos/localidade.service';
import { Localidade } from '../../../modelos/localidade';
import { EstudanteLocalidade } from '../../../modelos/estudanteLocalidade';
import { EstudanteLocalidadeService } from '../../../servicos/estudanteLocalidade.service';
import { ValidationErrorHandlerService } from '../../../servicos/validationErrorHandler.service';
import { v4 as uuidv4 } from 'uuid';

@Component({
  selector: 'app-criar-estudante',
  templateUrl: './criar.component.html',
  styleUrls: ['./criar.component.css']
})
export class CriarEstudanteComponent {
  novoEstudante: Estudante = {
    id: uuidv4(),
    nomeCompleto: '',
    dataNascimento: new Date(),
    localidade: {
      id: '',
      cidade: '',
      estado: '',
      logradouro: '',
      cep: '',
    }
  };
  novaLocalidade: Localidade = {
    id: uuidv4(),
    cidade: '',
    estado: '',
    logradouro: '',
    cep: ''
  };
  localidades: Localidade[] = [];
  localidadeId: string | undefined;
  novoEstudanteLocalidade: EstudanteLocalidade = {
    id: uuidv4(),
    estudanteId: '',
    estudante: {
      id: '',
      nomeCompleto: '',
      dataNascimento: new Date(),
    },
    localidadeId: '',
    localidade: {
      id: '',
      cidade: '',
      estado: '',
      logradouro: '',
      cep: '',
    }
  };
  alertaSucesso: boolean = false;
  alertaErro: boolean = false;
  mensagemAlerta: string = '';

  opcaoLocalidade: 'criar' | 'utilizar' | 'semCadastro' = 'semCadastro';

  constructor(
    private estudanteService: EstudanteService,
    private estudanteLocalidadeService: EstudanteLocalidadeService,
    private localidadeService: LocalidadeService,
    private validationErrorHandlerService: ValidationErrorHandlerService,
    private route: ActivatedRoute,
    private router: Router
  ) { }

  ngOnInit (): void {
    this.localidadeService.listarLocalidades().subscribe(
      (data: Localidade[]) => {
        this.localidades = data;
      },
      (error) => {
        console.error(error);
        // Trate o erro de forma apropriada, como exibindo uma mensagem para o usuário.
      }
    );
  }

  fecharAlerta (tipo: string): void {
    if (tipo === 'sucesso') {
      this.alertaSucesso = false;
    } else if (tipo === 'erro') {
      this.alertaErro = false;
    }
  }

  criarEstudante (): void {
    if (this.opcaoLocalidade === 'criar') {
      this.novoEstudante.localidade = this.novaLocalidade;
    } else {
      this.novoEstudante.localidade = undefined;
    }
    this.estudanteService.salvarEstudante(this.novoEstudante).subscribe(() => {
      if (this.localidadeId && this.novoEstudante) {
        if (this.opcaoLocalidade === 'utilizar') {
          this.novoEstudanteLocalidade.localidadeId = this.localidadeId;
          this.novoEstudanteLocalidade.estudanteId = this.novoEstudante.id;
          this.estudanteLocalidadeService.salvarEstudanteLocalidade(this.novoEstudanteLocalidade).subscribe(() => {
            this.alertaSucesso = true;
            this.mensagemAlerta = 'Estudante criado com sucesso!';
            this.router.navigate(['/estudantes']);
          }, (erro) => {
            this.alertaErro = true;
            this.mensagemAlerta = 'Erro ao associar localidade ao estudante. Detalhes: ' + erro.message;
          });
        }
      }
      this.alertaSucesso = true;
      this.mensagemAlerta = 'Estudante criado com sucesso!';
      this.router.navigate(['/estudantes']);
    }, (erro) => {
      console.log(erro.error.errors);
      const validationErrors = this.validationErrorHandlerService.validationErrors(erro.error.errors);
      this.alertaErro = true;
      this.mensagemAlerta = 'Erro ao criar estudante. Detalhes: ' + validationErrors.join(', ');
    });
  }

  handleValidationErrors (error: any): string[] {
    const validationErrors: string[] = [];

    if (error.status === 400 && error.errors) {
      for (const key in error.errors) {
        if (error.errors.hasOwnProperty(key)) {
          const messages = error.errors[key];
          for (const message of messages) {
            validationErrors.push(`${message.trim().replace(/\.$/, '').toLowerCase()}`);
          }
        }
      }
    } else {
      validationErrors.push('Erro desconhecido.');
    }

    return validationErrors;
  }
}
<div *ngIf="alertaSucesso" class="alert alert-success alert-dismissible fade show" role="alert">
  {{ mensagemAlerta }}
  <button type="button" class="close" (click)="fecharAlerta('sucesso')" aria-label="Fechar">
    <span aria-hidden="true">&times;</span>
  </button>
</div>
<div *ngIf="alertaErro" class="alert alert-danger alert-dismissible fade show" role="alert">
  {{ mensagemAlerta }}
  <button type="button" class="close" (click)="fecharAlerta('erro')" aria-label="Fechar">
    <span aria-hidden="true">&times;</span>
  </button>
</div>

<div>
  <h2>Cadastrar Estudante</h2>
  <form (submit)="criarEstudante()">
    <div class="form-row row">
      <div class="form-group col-md-6">
        <label for="nomeCompleto">Nome Completo:</label>
        <input id="nomeCompleto" type="text" name="nomeCompleto" class="form-control"
          [(ngModel)]="novoEstudante.nomeCompleto" required>
      </div>
      <div class="form-group col-md-3">
        <label for="dataNascimento">Data de Nascimento:</label>
        <input id="dataNascimento" type="date" name="dataNascimento" class="form-control"
          [(ngModel)]="novoEstudante.dataNascimento" required>
      </div>
    </div>
    <div class="form-row row">
      <div class="form-group col-md-12">
        <label>Vincular localidade:</label>
        <div class="form-check">
          <input class="form-check-input" type="radio" name="opcaoLocalidade" id="semCadastro"
            [(ngModel)]="opcaoLocalidade" value="semCadastro">
          <label class="form-check-label" for="semCadastro">Aluno sem cadastro vinculado</label>
        </div>
        <div class="form-check">
          <input class="form-check-input" type="radio" name="opcaoLocalidade" id="criarLocalidade"
            [(ngModel)]="opcaoLocalidade" value="criar" checked>
          <label class="form-check-label" for="criarLocalidade">Criar nova localidade</label>
        </div>
        <div class="form-check">
          <input class="form-check-input" type="radio" name="opcaoLocalidade" id="utilizarLocalidade"
            [(ngModel)]="opcaoLocalidade" value="utilizar">
          <label class="form-check-label" for="utilizarLocalidade">Utilizar localidade cadastrada</label>
        </div>
      </div>
    </div>
    <div class="form-row row" *ngIf="opcaoLocalidade === 'utilizar'">
      <div class="form-group col-md-9">
        <label for="localidade">Localidade:</label>
        <select id="localidade" name="localidade" class="form-control" [(ngModel)]="localidadeId">
          <option *ngFor="let localidade of localidades" [value]="localidade.id">{{ localidade.logradouro }}, {{
            localidade.cidade }} - {{ localidade.estado }}</option>
        </select>
      </div>
    </div>
    <div class="form-row row" *ngIf="opcaoLocalidade === 'criar'">
      <app-endereco-form [endereco]="novaLocalidade"></app-endereco-form>
    </div>
    <div class="form-row row">
      <div class="form-group col-md-3">
        <button type="submit" class="btn btn-primary mt-4">Criar</button>
      </div>
    </div>
  </form>
</div>
