import { Component } from '@angular/core';
import { AuthService } from '../app/servicos/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  constructor(private authService: AuthService, private router: Router) { }
  title = 'site';

  isLoggedIn(): boolean {
    return this.authService.isLoggedIn();
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/']);
  }
}
<div class="container-fluid">
  <div class="row">
    <!-- Menu lateral -->
    <div class="col-md-3">
      <header>
        <h1>Gerenciador</h1>
        <nav>
          <ul class="nav flex-column">
            <li class="nav-item"><a class="nav-link" routerLink="/home/tela">Início</a></li>
            <li class="nav-item"><a class="nav-link" routerLink="/estudantes/tela">Estudantes</a></li>
            <li class="nav-item"><a class="nav-link" routerLink="/localidades/tela">Localidades</a></li>
            <li *ngIf="isLoggedIn()" class="nav-item">
              <a class="nav-link cursor-pointer highlight-logout" (click)="logout()">Logout</a>
            </li>
            <li *ngIf="!isLoggedIn()" class="nav-item">
              <a class="nav-link cursor-pointer highlight-logout" routerLink="/login">Login</a>
            </li>
          </ul>
        </nav>
      </header>
    </div>

    <!-- Conteúdo principal -->
    <div class="col-md-9">
      <main>
        <router-outlet></router-outlet>
      </main>
    </div>
  </div>
</div>
