import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-endereco-form',
  templateUrl: './enderecoForm.component.html',
  styleUrls: ['./enderecoForm.component.css']
})
export class EnderecoFormComponent {
  @Input() endereco: any; // ou crie uma interface para definir a estrutura do endereço

  buscarDetalhesCEP(): void {
    const cep = this.novaLocalidade.cep.replace(/\D/g, '');
    if (cep.length === 8) {
      this.localidadeService.obterDetalhesCEP(cep).subscribe(
        (detalhes: any) => {
          this.novaLocalidade.logradouro = detalhes.logradouro;
          this.novaLocalidade.cidade = detalhes.localidade;
          this.novaLocalidade.estado = detalhes.uf;
        },
        (error) => {
          this.alertaErro = true;
          this.mensagemAlerta = 'Erro ao buscar CEP. Detalhes: ' + error.message;
        }
      );
    }
  }
}
