import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LocalidadeService } from '../../../servicos/localidade.service';
import { Localidade } from '../../../modelos/localidade';
import { ValidationErrorHandlerService } from '../../../servicos/validationErrorHandler.service';
import { v4 as uuidv4 } from 'uuid';

@Component({
  selector: 'app-criar-localidade',
  templateUrl: './criar.component.html',
  styleUrls: ['./criar.component.css']
})
export class CriarLocalidadeComponent {
  novaLocalidade: Localidade = {
    id: uuidv4(),
    cidade: '',
    estado: '',
    logradouro: '',
    cep: ''
  };
  alertaSucesso: boolean = false;
  alertaErro: boolean = false;
  mensagemAlerta: string = '';

  constructor(
    private localidadeService: LocalidadeService,
    private validationErrorHandlerService: ValidationErrorHandlerService,
    private router: Router
  ) { }

  fecharAlerta (tipo: string): void {
    if (tipo === 'sucesso') {
      this.alertaSucesso = false;
    } else if (tipo === 'erro') {
      this.alertaErro = false;
    }
  }

  criarLocalidade (): void {
    this.localidadeService.salvarLocalidade(this.novaLocalidade).subscribe(() => {
      this.router.navigate(['/localidades']);
    },
      (error) => {
        this.alertaErro = true;
        this.mensagemAlerta = 'Erro ao salvar localidade. Detalhes: ' + error.message;
      });
  }
}
<div *ngIf="alertaSucesso" class="alert alert-success alert-dismissible fade show" role="alert">
  {{ mensagemAlerta }}
  <button type="button" class="close" (click)="fecharAlerta('sucesso')" aria-label="Fechar">
    <span aria-hidden="true">&times;</span>
  </button>
</div>
<div *ngIf="alertaErro" class="alert alert-danger alert-dismissible fade show" role="alert">
  {{ mensagemAlerta }}
  <button type="button" class="close" (click)="fecharAlerta('erro')" aria-label="Fechar">
    <span aria-hidden="true">&times;</span>
  </button>
</div>

<div>
  <h2>Cadastrar Localidade</h2>
  <form (submit)="criarLocalidade()">
    <app-endereco-form [endereco]="novaLocalidade"></app-endereco-form>
    <div class="form-row row">
      <div class="form-group col-md-2">
        <button type="submit" class="btn btn-primary mt-4">Criar</button>
      </div>
    </div>
  </form>
</div>
