import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { LocalidadeService } from '../../../servicos/localidade.service';
import { Localidade } from '../../../modelos/localidade';

@Component({
  selector: 'app-detalhes',
  templateUrl: './detalhes.component.html',
  styleUrls: ['./detalhes.component.css']
})
export class DetalhesLocalidadeComponent implements OnInit {
  localidade: Localidade | undefined;

  alertaSucesso: boolean = false;
  alertaErro: boolean = false;
  mensagemAlerta: string = '';

  constructor(private localidadeService: LocalidadeService, private route: ActivatedRoute) { }

  ngOnInit (): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.localidadeService.obterLocalidadePorId(id).subscribe(
        (data: Localidade) => {
          this.localidade = data;
        },
        (error) => {
          this.alertaErro = true;
          this.mensagemAlerta = 'Erro ao obter localidade. Detalhes: ' + error.message;
        }
      );
    }
  }

  fecharAlerta (tipo: string): void {
    if (tipo === 'sucesso') {
      this.alertaSucesso = false;
    } else if (tipo === 'erro') {
      this.alertaErro = false;
    }
  }
}
<div *ngIf="alertaSucesso" class="alert alert-success alert-dismissible fade show" role="alert">
  {{ mensagemAlerta }}
  <button type="button" class="close" (click)="fecharAlerta('sucesso')" aria-label="Fechar">
    <span aria-hidden="true">&times;</span>
  </button>
</div>
<div *ngIf="alertaErro" class="alert alert-danger alert-dismissible fade show" role="alert">
  {{ mensagemAlerta }}
  <button type="button" class="close" (click)="fecharAlerta('erro')" aria-label="Fechar">
    <span aria-hidden="true">&times;</span>
  </button>
</div>

<div *ngIf="localidade" class="container">
  <h2>Detalhes do Localidade</h2>
  <div class="card">
    <div class="card-body">
      <p class="card-text"><strong>ID:</strong> {{ localidade.id }}</p>
      <p class="card-text"><strong>Cidade:</strong> {{ localidade.cidade }}</p>
      <p class="card-text"><strong>Estado:</strong> {{ localidade.estado }}</p>
      <p class="card-text"><strong>Logradouro:</strong> {{ localidade.logradouro }}</p>
      <p class="card-text"><strong>CEP:</strong> {{ localidade.cep }}</p>
    </div>
  </div>
</div>
