import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable, catchError, map, of } from 'rxjs';
import { Router } from '@angular/router';

import { AppConfig } from '../app.config';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private token: string = '';
  private readonly tempoLogin: number = 5;

  constructor(private http: HttpClient, private router: Router, private appConfig: AppConfig) { }

  login (login: string, senha: string): Observable<any> {
    const params = new HttpParams()
      .set('login', login)
      .set('senha', senha);

    return this.http.post<any>(`${this.appConfig.getApiUrl()}/auth/login`, {}, { params });
  }

  setTokenAndScheduleLogout (token: string) {
    this.token = token;

    const expirationTime = new Date().getTime() + this.tempoLogin * 60 * 1000;

    localStorage.setItem('token', token);
    localStorage.setItem('tokenExpiration', expirationTime.toString());

    setTimeout(() => {
      this.removeToken();
    }, this.tempoLogin * 60 * 1000);
  }

  removeToken () {
    this.token = '';
    localStorage.removeItem('token');
    localStorage.removeItem('tokenExpiration');
    this.router.navigate(['/login']);
  }

  getToken (): string {
    return this.token || localStorage.getItem('token') || '';
  }

  isLoggedIn (): boolean {
    console.log(!!this.getToken());
    // this.isAuthorize().subscribe(isAuthorize => {
    //   if (isAuthorize) {
    //     console.log('O usuário está logado');
    //   } else {
    //     console.log('O usuário não está logado');
    //   }
    // });

    return !!this.getToken();
  }

  isAuthorize (): Observable<boolean> {
    return this.http.post<any>(`${this.appConfig.getApiUrl()}/auth/isLoggedIn`, {})
      .pipe(
        map(response => {
          if (response && response.hasOwnProperty('status') && response.status === 200) {
            return true;
          } else {
            return false;
          }
        }),
        catchError(error => {
          return of(false);
        })
      );
  }

  logout () {
    this.token = '';
    localStorage.removeItem('token');
  }
}
